// ResultsTable.js
import React, { useState } from 'react';
import { useDrop } from 'react-dnd';

const DropZone = ({ onDrop, droppedItems, title }) => {
  const [, ref] = useDrop(() => ({
    accept: 'DATASET',
    drop: (item) => onDrop(item),
  }));

  return (
    <div className="drop-zone">
      <h4>{title}</h4>
      <div ref={ref} className="drop-area">
        {droppedItems.map((item, index) => (
          <div key={index} className="dropped-item">
            {item.name}
          </div>
        ))}
      </div>
    </div>
  );
};

const ResultsTable = ({ data, onDatasetDrop, droppedItemsZone1, droppedItemsZone2 }) => {
  const handleDropZone1 = (item) => {
    onDatasetDrop(item, 1);
  };

  const handleDropZone2 = (item) => {
    onDatasetDrop(item, 2);
  };

  return (
    <div>
      <div className="drop-zones">
        <DropZone onDrop={handleDropZone1} droppedItems={droppedItemsZone1} title="Drop Zone 1" />
        <DropZone onDrop={handleDropZone2} droppedItems={droppedItemsZone2} title="Drop Zone 2" />
      </div>
      <table>
        <thead>
          <tr>
            {data.length > 0 && Object.keys(data[0]).map((key) => (
              <th key={key}>{key}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {Object.values(row).map((value, i) => (
                <td key={i}>{value}</td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ResultsTable;
