import React, { Component } from 'react';
import axisos from 'axios';
import Header from 'components/Header';
import Sidebar  from 'components/Sidebar';

class App extends Component {
  state = {
    activeMenuItem: 'employees', // Default active menu item
  };

  handleMenuItemClick = (menuItem) => {
    this.setState({ activeMenuItem: menuItem });
  };

  renderContent() {
    const { activeMenuItem } = this.state;

    switch (activeMenuItem) {
      case 'employees':
        return <Employees />;
      case 'salary':
        return <SalarySuggestion />;
      // Add cases for other menu items as needed
      default:
        return null;
    }
  }

  render() {
    return (
      <div className="App">
        <div className="container-fluid">
          <div className="row">
            {/* Pass handleMenuItemClick as onItemClick prop */}
            <Sidebar onItemClick={this.handleMenuItemClick} />
            <div className="col-md-9">
              {this.renderContent()}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
